import React, { useState, useEffect } from 'react';
import MenuElement from './MenuElement.js';
import axios from 'axios';

import './LeftMenu.css';

const refresh = (props, setElements, setState) => {
  setState(null);
  setElements(<div className="loading">Refreshing...</div>)
  axios.get("http://localhost:3001/api/get/recipes")
  .then( (res) => {
    if (res.status !== 200) {
      return setElements(<div className="error">Loading Failed!</div>);
    }

    return setElements(res.data.map( (r) => {
      return <MenuElement val={r.title} key={r.id} elementId={r.id} onClick={() => props.setLoadedElement(r.id)} src={`http://localhost:3001/api/get/image/${r.icon}`} setState={setState}/>
    }));
  })

  return;
}

const createNew = (setState) => {
  return (
    <div className = "new-element">
      <h1>Create New Recipe</h1>
      <form method = "POST">
        <input type="text" id="title" placeholder="title"></input>

      </form>
      <textarea id="description"></textarea>

      <div className = "close" onClick={() => setState(null)}>X</div>
    </div>)
};

const editSelected = (setState) => {
  return (
    <div className = "new-element">
      <h1>Create New Recipe</h1>
      <form method = "POST">
        <input type="text" id="title" placeholder="title"></input>

      </form>
      <textarea id="description"></textarea>

      <div className = "close" onClick={() => setState(null)}>X</div>
    </div>)
}

const LeftMenu = (props) => {
  const [state, setState] = useState(null);
  const [elements, setElements] = useState(<div className="loading">Loading Data...</div>);
  

  useEffect( () => {
    axios.get("http://localhost:3001/api/get/recipes")
      .then( (res) => {
        if (res.status !== 200) {
          return setElements(<div className="error">Loading Failed!</div>);
        }

        return setElements(res.data.map( (r) => {
          return <MenuElement val={r.title} key={r.id} elementId={r.id} onClick={() => props.setLoadedElement(r.id)} src={`http://localhost:3001/api/get/image/${r.icon}`} />
        }));
      })
  }, [props]);
  
  return (
  <div className="menu left">
    <div className="banner">
      Menu
    </div>
    <div className="elements">
      {elements}
    </div>
    <div className="controls">
      <div className="button refresh" onClick={() => setState(1)}><img src="RefreshItem.svg" alt="refresh"/></div>
      <div className="button add" onClick={() => setState(2)}><img src="AddItem.svg" alt="refresh"/></div>
      
    </div>

    { state === 1 ? refresh(props, setElements, setState) : null }
    { state === 2 ? createNew(setState) : null }
    {/* state === 3 ? editSelected(setState) : null */}
  </div>
  );
}

export default LeftMenu;
