import React, { useState } from 'react';
import axios from 'axios';
import './Content.css';

const Content = (props) => {
  const [title, setTitle] = useState(null);
  const [description, setDescription] = useState(null);
  const [ingredients, setIngredients] = useState(null);
  const [steps, setSteps] = useState(null);
  const [images, setImages] = useState(null);

  if (props.loadedElement !== null) {
    axios.get(`http://localhost:3001/api/get/recipes/${props.loadedElement}`)
      .then((res) => {
        setTitle(res.data.title);
        setDescription(res.data.description);
        setIngredients(JSON.parse(res.data.ingredients));
      })
    return (
      <div className="content">
        <div className="box">
          <div className="recipe title">
            {title}
          </div>
          <div className="recipe image">
            <div className="arrow left"><span>{'<'}</span></div>
            {images}
            <div className="arrow right"><span>{'>'}</span></div>
          </div>
          <div className="recipe description">
            <h2>Description</h2>
            {description}
            </div>
          <div className="recipe ingredients">
            <h2>Ingredients</h2>
            {ingredients}
          </div>
          <div className="recipe steps">
            <h2>Steps</h2>
            {steps}
          </div>
        </div>
      </div>
    )
  }
  return (
    <div className="content" onClick={()=>console.log(props.loadedElement)}>
      <div className="box">
        <h1>Select an Recipe From Menu on the left</h1>
      </div>
    </div>)
}

export default Content;
