    //       }
    //     );
    //   })
    //   .then( (res) => {
    //     setElements(res)
    //   });
  })

  return (
    <div className="menu left">
      <div className="banner">
        Menu
      </div>
      <div className="elements">
        {elements}
      </div>
    </div>
  )
}

export default LeftMenu;
