import React, { useState, useEffect } from 'react';
import MenuElement from './MenuElement.js';
import axios from 'axios';

import './LeftMenu.css';

const LeftMenu = (props) => {
  const [elements, setElements] = useState();
  const [selectedElement, setElement] = useState(null);

  const elementOnClick = (id) => {
    setElement(id);
  }

  useEffect(() => {
    axios.get("http://localhost:3001/api/get/recipes")
      .then( (res) => {
        return res.data.map( (r) => {
          let title = r.title.length > 20 ? r.title.slice(0,15).concat('...') : r.title;
          return <MenuElement onClick={()=>elementOnClick(r.id)} val={title} src="placeholder.jpg" key={r.id}/>
          }
        );
      })
      .then( (res) => {
        setElements(res)
      });
  })

  return (
    <div className="menu left" onClick={props.onClick = selectedElement}>
      <div className="banner">
        Menu
      </div>
      <div className="elements">
        {elements}
      </div>
    </div>
  )
}

export default LeftMenu;
