import React, { useState, useEffect } from 'react';
import MenuElement from './MenuElement.js';
import axios from 'axios';

import './LeftMenu.css';

const fetchData = async (props) => {
  try {
    return await axios.get("http://localhost:3001/api/get/recipes")
    .then( (res) => {
      return res.data.map( (r) => {
        return <MenuElement val={r.title} key={r.id} elementId={r.id} onClick={() => props.setLoadedElement(r.id)} src="placeholder.jpg"></MenuElement>
      });
    })
  } catch (e) {
    return false;
  }
}

const LeftMenu = (props) => {
  const [elements, setElements] = useState();

  const data = fetchData(props);
  console.log(data);
  if (!data) {
    return (
      <div className="menu left">
        <div className="banner">
          Menu
        </div>
        <div className="elements">
          <div className="loading-error">AnError occured</div>
        </div>
      </div>
    )
  }

  return (
    <div className="menu left">
      <div className="banner">
        Menu
      </div>
      <div className="elements">
        {elements}
      </div>
    </div>
  )
}

export default LeftMenu;
