import React, { useState, useEffect } from 'react';
import MenuElement from './MenuElement.js';
import axios from 'axios';

import './LeftMenu.css';

// const getData = async (props) => {
//   try {
//     return await axios.get("http://localhost:3001/api/get/recipes")
//     .then( (res) => {
//       return res.data.map( (r) => {
//         return <MenuElement val={r.title} key={r.id} elementId={r.id} onClick={() => props.setLoadedElement(r.id)} src="placeholder.jpg"></MenuElement>
//       });
//     })
//   } catch (e) {
//     return false;
//   }
// }

const LeftMenu = (props) => {
  const [elements, setElements] = useState(<div className="loading">Loading Data...</div>);
  useEffect( () => {
    axios.get("http://localhost:3001/api/get/recipes")
      .then( (res) => {
        if (res.status !== 200) {
          return setElements(<div className="error">Loading Failed!</div>);
        }

        return setElements(res.data.map( (r) => {
          return <MenuElement val={r.title} key={r.id} elementId={r.id} onClick={() => props.setLoadedElement(r.id)} src={`http://localhost:3001/api/get/image/${r.icon}`} />
        }));
      })
  }, [props]);
  
  return (
  <div className="menu left">
    <div className="banner">
      Menu
    </div>
    <div className="elements">
      {elements}
    </div>
    <div className="controls">
      <div className="button refresh"><img src="vectorpaint.svg" alt="refresh"/></div>
      <div className="button add">Add</div>
      <div className="button edit">Edit</div>
    </div>
  </div>
  );
}

export default LeftMenu;
